// Retrieve input from JSON, fallback to an empty string if it's not defined
const data = $json.emails || '';

// Ensure input is a string
const inputString = Array.isArray(data) ? data.join(' ') : String(data);

// Improved regex to match emails and exclude image extensions like .png, .jpg, .gif, .jpeg
const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(?!png|jpg|gif|jpeg)[a-zA-Z]{2,}/g;

// Match valid email addresses
let emails = inputString.match(emailRegex) || []; // Default to empty array if no match

// Deduplicate the email addresses
emails = [...new Set(emails)];

// Log output for debugging in n8n
console.log("Matched Emails:", emails);

// Limit the result to the first 5 email addresses
emails = emails.slice(0, 2);

// Return the cleaned list of emails in n8n-compatible format
return { json: { emails: emails } };

let emails = []; // Initialize emails as an empty array

try {
  // Extract data from input
  const data = $json.data;

  // Ensure data exists and is a string before proceeding
  if (typeof data === 'string') {
    // Define a regular expression to match email addresses
    const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(?!png|jpg|gif|jpeg)[a-zA-Z]{2,}/g;

    // Use the regex to extract emails from the input data
    emails = data.match(emailRegex) || []; // Fallback to an empty array if no matches are found
  } else {
    console.log("Data is not a string. Skipping email extraction.");
  }
} catch (error) {
  // Handle and log any unexpected errors
  console.error("An error occurred during email extraction:", error.message);
}

// Log the extracted emails for debugging purposes
console.log("Extracted Emails:", emails);

// Return the result in the desired format
return {
  json: {
    emails: emails // Return the array of emails (even if empty due to errors)
  }
};
