parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "options": {
      "type": "string",
      "description": "Options for the email, including appendAttribution and senderName"
    },
    "to": {
      "type": "string",
      "description": "The recipient email address"
    },
    "subject": {
      "type": "string",
      "description": "The subject of the email"
    },
    "message": {
      "type": "string",
      "description": "The content of the email message"
    },
    "bccList": {
      "type": "string",
      "description": "The list of BCC (blind carbon copy) recipients"
    },
    "ccList": {
      "type": "string",
      "description": "The list of CC (carbon copy) recipients"
    }
  }
}

{
  "type": "object",
  "properties": {
    "start": {
      "type": "string",
      "description": "The start date or time for the event"
    },
    "end": {
      "type": "string",
      "description": "The end date or time for the event"
    },
    "attendees": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "A list of attendees for the event"
    },
    "description": {
      "type": "string",
      "description": "The description of the event"
    },
    "summary": {
      "type": "string",
      "description": "The summary or title of the event"
    }
  }
}

{
  "type": "object",
  "properties": {
    "fileContent": {
      "type": "string",
      "description": "The content of the file"
    },
    "fileName": {
      "type": "string",
      "description": "The name of the file, including the extension"
    }
  }
}
